import React from "react";

import Navbar from "../../components/navbar";
import Body from "../../components/body";
import SocialLinks from "../../components/socialLinks";

import { Typography, Grid } from "@material-ui/core";

import withStyles from "./styles";

class Contact extends React.Component {
  renderSocialLinks = () => {
    const { classes } = this.props;
    return (
      <div className={classes.socialLinkContainer}>
        <SocialLinks />
      </div>
    );
  };

  render() {
    const { classes } = this.props;
    return (
      <div>
        <Navbar />
        <Body>
          {this.renderSocialLinks()}
          <Grid
            container
            direction="column"
            justify="center"
            alignContent="center"
          >
            <div className={classes.container}>
              <Grid container>
                <Grid item xs={6} className={classes.paraContainer}>
                  <Typography
                    variant="h5"
                    inline={true}
                    className={classes.number}
                  >
                    04.
                  </Typography>
                  <Typography
                    variant="h4"
                    inline={true}
                    className={classes.pageTitle}
                  >
                    Contact Me
                  </Typography>
                </Grid>
              </Grid>
              <form
                action="https://formspree.io/drewblincoe7@gmail.com"
                method="POST"
                className={classes.form}
              >
                <Grid container>
                  <Grid item xs={6} className={classes.inputContainer}>
                    <input type="text" name="name" placeholder="Name" />
                  </Grid>
                  <Grid item xs={6} className={classes.inputContainer}>
                    <input type="email" name="_replyto" placeholder="Email" />
                  </Grid>
                </Grid>

                <textarea name="message" placeholder="Enter message here..." />
                <input type="submit" value="Send" />
              </form>
            </div>
          </Grid>
        </Body>
      </div>
    );
  }
}

export default withStyles(Contact);
