import React from "react";
import classNames from "classnames";

import { Grid } from "@material-ui/core";

import withStyles from "./styles";

class Overlay extends React.Component {
  state = {
    hover: false,
    src: ""
  };

  componentDidMount() {
    if (this.props.site === "torchlite") {
      this.setState({
        src: "/static/torchlite.png"
      });
    }

    if (this.props.site === "ovazi") {
      this.setState({
        src: "/static/ovazi.png"
      });
    }
  }

  handleMouseEnter = () => {
    this.setState({
      hover: true
    });
  };

  handleMouseLeave = () => {
    this.setState({
      hover: false
    });
  };

  render() {
    const { src } = this.state;
    return (
      <Grid item xs={6} className={classes.outer}>
        <img src={"/static/torchlite.png"} className={classes.img} />
        <div
          className={classNames({
            [classes.overlay]: true,
            [classes.visible]: hover
          })}
          onMouseEnter={this.handleMouseEnter}
          onMouseLeave={this.handleMouseLeave}
        />
      </Grid>
    );
  }
}

export default withStyles(Overlay);
