import { withStyles } from "@material-ui/core/styles";

export default withStyles(theme => {
  const { colors } = theme.palette;

  return {
    outer: {
      position: "relative",
      display: "flex",
      justifyContent: "center",
      alignContent: "center"
    },
    overlay: {
      background: colors.main,
      position: "absolute",
      top: 0,
      height: "100%",
      width: "100%",
      opacity: 0
    },
    visible: {
      opacity: 0.8
    },
    img: {
      width: "100%",
      height: "100%"
    },
    title: {
      textAlign: "center",
      color: colors.secondary
    }
  };
});
