import React from "react";
import classNames from "classnames";

import { Grid, Typography, Button } from "@material-ui/core";

import withStyles from "./styles";

class Overlay extends React.Component {
  state = {
    hover: false,
    src: "",
    title: ""
  };

  componentDidMount() {
    if (this.props.site === "torchlite") {
      this.setState({
        src: "/static/torchlite.png",
        title: "Torchlite"
      });
    }

    if (this.props.site === "ovazi") {
      this.setState({
        src: "/static/ovazi.png",
        title: "Ovazi"
      });
    }
  }

  handleMouseEnter = () => {
    this.setState({
      hover: true
    });
  };

  handleMouseLeave = () => {
    this.setState({
      hover: false
    });
  };

  render() {
    const { classes } = this.props;
    const { src, hover, title } = this.state;
    return (
      <Grid item xs={12} sm={6} className={classes.outer}>
        <img src={src} className={classes.img} />
        <div
          className={classNames({
            [classes.overlay]: true,
            [classes.visible]: hover
          })}
          onMouseEnter={this.handleMouseEnter}
          onMouseLeave={this.handleMouseLeave}
        >
          <Typography variant="h4" className={classes.title}>
            {title}
          </Typography>
          <Button variant="outlined" color="primary" className={classes.button}>
            More Info
          </Button>
        </div>
      </Grid>
    );
  }
}

export default withStyles(Overlay);
