import { createMuiTheme } from "@material-ui/core/styles";

const colors = require("./colors");

export default createMuiTheme({
  mixins: {
    clearfix: {
      "&:after": {
        content: "",
        display: "table",
        clear: "both"
      }
    }
  },
  typography: {
    fontFamily: "Roboto",
    useNextVariants: true
  },
  palette: {
    colors,
    background: {
      default: colors.main
    },
    primary: {
      main: colors.main
    }
  }
});
