import classNames from "classnames";

import Navbar from "../../components/navbar";
import SocialLinks from "../../components/socialLinks";
import Body from "../../components/body";

import { Grid, Typography } from "@material-ui/core/";

import withStyles from "./styles";

class Portfolio extends React.Component {
  state = {
    hover: false,
    hoverNumber: null
  };

  handleMouseEnter(evt, num) {
    this.setState({
      hover: true,
      hoverNumber: num
    });
  }

  handleMouseLeave = () => {
    this.setState({
      hover: false,
      hoverNumber: null
    });
  };

  renderSocialLinks = () => {
    const { classes } = this.props;
    return (
      <div className={classes.socialLinkContainer}>
        <SocialLinks />
      </div>
    );
  };

  render() {
    const { classes } = this.props;
    const { hover, hoverNumber } = this.state;
    return (
      <div>
        <Navbar />
        <Body>
          {this.renderSocialLinks()}
          <Grid
            container
            direction="column"
            justify="center"
            alignContent="center"
          >
            <div className={classes.container}>
              <Grid container>
                <Grid item xs={6} className={classes.paraContainer}>
                  <Typography
                    variant="h5"
                    inline={true}
                    className={classes.number}
                  >
                    03.
                  </Typography>
                  <Typography
                    variant="h4"
                    inline={true}
                    className={classes.pageTitle}
                  >
                    Portfolio
                  </Typography>
                </Grid>
                <Grid container wrap className={classes.workContainer}>
                  <Grid item xs={6} className={classes.outer}>
                    <img
                      src={"/static/torchlite.png"}
                      className={classes.img}
                    />
                    <div
                      className={classNames({
                        [classes.overlay]: true,
                        [classes.visible]: hover
                      })}
                      onMouseEnter={this.handleMouseEnter}
                      onMouseLeave={this.handleMouseLeave}
                    />
                  </Grid>
                  <Grid item xs={6} className={classes.outer}>
                    <img src={"/static/ovazi.png"} className={classes.img} />
                    <div
                      className={classNames({
                        [classes.overlay]: true,
                        [classes.visible]: hover
