import { withStyles } from "@material-ui/core/styles";

export default withStyles(theme => {
  const colors = theme.palette.colors;

  return {
    container: {
      display: "flex",
      flexDirection: "column",
      height: "100%",
      width: "100%",
      justifyContent: "center",
      alignItems: "center"
    },
    introContainer: {
      display: "flex",
      flexDirection: "column",
      justifyContent: "flex-start"
    },
    intro: {
      color: colors.secondary,
      fontFamily: "Roboto",
      fontSize: "20px",
      lineHeight: "1.25"
    },
    name: {
      color: colors.white,
      fontSize: "80px",
      lineHeight: "1.25"
    },
    title: {
      color: colors.main50,
      fontSize: "60px",
      lineHeight: "1.25"
    },
    description: {
      width: "150px",
      color: colors.main50,
      fontSize: "20px"
    }
  };
});
