import { withStyles } from "@material-ui/core/styles";

export default withStyles(theme => {
  const { colors } = theme.palette;

  return {
    outer: {
      position: "relative",
      margin: 8
    },
    overlay: {
      background: colors.secondary,
      position: "absolute",
      top: 0,
      height: "100%",
      width: "100%",
      opacity: 0
    },
    visible: {
      opacity: 0.9
    },
    img: {
      width: "100%",
      height: "100%"
    }
  };
});
